/**
 * Represents an Ext class definition.
 * @param {Object} callExpression Ext.define call expression.
 */
function ExtClass(callExpression) {
    this.ce = callExpression;
    this.node = callExpression.node;
}

ExtClass.prototype.getClassName = function() {
    return this.ce.getArgs()[0].value.value;
}

ExtClass.prototype._getProp = function(name) {
    var props = this.ce.getArgs()[1].properties;

    if (!props) {
        return;
    }

    return props.filter(function(prop) {
        return prop.key && prop.key.name === name;
    }).pop();
}

ExtClass.prototype.getConfig = function(name) {
    var prop = this._getProp(name);

    if (!prop || !prop.value) {
        return;
    }

    return prop.value.value;
}

module.exports = ExtClass;
